mapper.select:
SELECT id, name, product_desc, img_addr, normal_price, promotion_price, priority, point, create_time, update_time, enable_status, shop_id, product_category_id FROM tb_product
and product_category_id = #{productCondition.productCategory.id}
and shop_id = #{productCondition.shop.id}
and enable_status = #{productCondition.enableStatus}
and name like '%${productCondition.name}%'
ORDER BY priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) 
FROM
tb_product


			and product_category_id = #{productCondition.productCategory.id}


			and shop_id = #{productCondition.shop.id}


			and enable_status = #{productCondition.enableStatus}


			and name like '%${productCondition.name}%'

mapper.update:
UPDATE tb_product
name = #{name}, product_desc=#{productDesc}, img_addr=#{imgAddr}, normal_price=#{normalPrice}, promotion_price=#{promotionPrice}, priority=#{priority}, update_time=#{updateTime}, enable_status=#{enableStatus}, product_category_id=#{productCategory.id}
WHERE id = #{id}
